class Controllor:    def __init__(self, conf):        self.__routesConf = {}        return    def add_routes_conf(self, blueprint, routeconf=None):        if routeconf is None or blueprint == "":            return        self.__routesConf[blueprint] = routeconf    def get_route_conf(self, blueprint):        if blueprint is "":            return        return self.__routesConf.get(blueprint, default=None)__ctl = {}def get_ctl():    return __ctldef init_ctl(conf):    global __ctl    __ctl = Controllor(conf)